import React from "react";
import "./displayError.css";

let hid =() =>{
  document.getElementById("pop")
    .classList
    .toggle("close");
}

const DisplayError = ({title, text}) =>{
  if(text){
    {
      document.getElementById("pop")
        .classList
        .toggle("show");

    }
  }
  return(
    <div id="pop" class="popup">
      <div class="innerPopup">
    <article>
            <h3> ${title} </h3>
        <body>
            <p>${text}</p>
        </body>
        <footer>
          <p id="close" onClick={hid} >x</p>
        </footer>
    </article>
      </div>
    </div>

  )
}


export default DisplayError;
